// 根据合约方法处理参数类型

func GetAbiAndArgs(contractAddr, abiContent, params string, args []interface{}) (abi.ABI, []interface{}, error) {
	// Abi转化
	contractAbi, err := StringToAbi(abiContent)
	if err != nil {
		if abiContent != "" {
			return contractAbi, nil, err
		}
		contractAbi = Erc20Abi
	}
	methodHash := "0x"
	method, exist := contractAbi.Methods[params]
	if exist {
		methodHash += crypto.Keccak256Hash([]byte(method.Sig)).Hex()[:10]
	}
	fmt.Printf("GetAbiAndArgs method: %+v\n, exist: %+v\n", method, exist)
	var argsNew []interface{}
	abiParam := method.Inputs
	for i, v := range abiParam {
		arg := ChangeArgType(contractAddr, v.Type, args[i])
		_, ok := arg.(error)
		if arg == nil || ok {
			continue
		}
		argsNew = append(argsNew, arg)
	}
	// 检查参数数量是否匹配
	if len(argsNew) != len(abiParam) {
		err := fmt.Errorf("the args len not enough")
		return contractAbi, nil, err
	}
	return contractAbi, argsNew, nil
}

func ChangeArgType(contractAddr string, argType abi.Type, arg interface{}) interface{} {
	argStr := arg.(string)
	switch argType.T {
	case abi.AddressTy:
		addr := EthAddressChange(argStr)
		if addr.String() != argStr {
			return nil
		}
		return addr
	case abi.UintTy:
		val, ok := big.NewInt(0).SetString(argStr, 10)
		if !ok {
			return nil
		}
		return val
	case abi.SliceTy:
		//fmt.Printf("argType Elem: %+v, argStr: %+v\n", (argType.Elem != nil), argStr)
		if argType.Elem != nil {
			var argSlice []interface{}
			err := json.Unmarshal([]byte(argStr), &argSlice)
			if err != nil {
				fmt.Printf("ChangeArgType json.Unmarshal err: %+v\n", err)
				return nil
			}
			return ChangeSliceByType(contractAddr, *argType.Elem, argSlice)
		}
		return nil
	default:
		fmt.Printf("ChangeArgType not case: %+v\n", argType)
	}
	return nil
}

func ChangeSliceByType(contractAddr string, argType abi.Type, arg []interface{}) interface{} {
	//fmt.Printf("argType: %+v\n", argType.T)
	switch argType.T {
	case abi.AddressTy:
		argSlice := make([]common.Address, len(arg))
		for i, v := range arg {
			argSlice[i] = common.HexToAddress(v.(string))
		}
		return argSlice
	case abi.TupleTy:
		switch contractAddr {
		case "ContractAddress":
		}
	}
	return nil
}

func GetAbiStrByContract(contract string) (string, error) {
	abiContent, ok := AbiMap[contract]
	if !ok {
		return "", fmt.Errorf("not found contract abi")
	}
	return abiContent, nil
}

func GetAbiByContract(contract string) (abi.ABI, error) {
	abiContent, ok := AbiMap[contract]
	if !ok {
		return abi.ABI{}, fmt.Errorf("not found contract abi")
	}
	return StringToAbi(abiContent)
}

func StringToAbi(abiContent string) (abi.ABI, error) {
	return abi.JSON(strings.NewReader(abiContent))
}
